    <h1>Hi Welcome Back</h1>
     <h3>Login Here</h3>
      <form [formGroup]="loginForm" (ngSubmit)="login()">
        <div>
          <label for="email">Email</label>
          <input  formControlName="email" required type="text">
          <label for="password">Password</label>
          <input formControlName="email" type="password" required>
        </div>
        <button>Submit</button>
        <span>Don't have an account? 
        <a routerLink="/signUp">signUp</a></span>
      </form>
  </div>
 -->
  <div class="container">
    <form [formGroup]="loginForm" (ngSubmit)="login()" class="form-box">
        <h3>Login</h3>
        <div class="form-group">
            <label>Email</label>
            <input type="text" formControlName="email" class="form-control mb-3 mt-3" placeholder="Enter Email"/>
        </div>

        <div class="form-group">
            <label>Password</label>
            <input type="password" formControlName="password" class="form-control mb-3 mt-3" placeholder="Enter Password"/>
        </div>

        <span>Create New Account </span>
        <a routerLink="/signUp">SignUp</a>
        <br/>
        <button type="submit" class="btn btn-success">Login</button>
    </form>
</div>
